import arcadeimport mathclass HUD:    def __init__(self, maze, tile_size):        self.maze = maze        self.tile_size = tile_size    def draw(self, player_x, player_y, player_angle, colors, score, show_score, show_map):        # Отрисовка мини-карты        if show_map:            self.draw_minimap(player_x, player_y, player_angle, colors)        # Отображение текста с очками        if show_score:            self.draw_score(score)    def draw_minimap(self, player_x, player_y, player_angle, colors):        # Размеры мини-карты        minimap_width = 200        minimap_height = 200        minimap_x = 10        minimap_y = 10        # Отрисовка фона мини-карты        arcade.draw_rectangle_filled(minimap_width / 2,minimap_height / 2, minimap_width, minimap_height, arcade.color.BLACK)        # Отрисовка лабиринта на мини-карте        for y in range(len(self.maze)):            for x in range(len(self.maze[y])):                if self.maze[y][x] == '#':                    color = colors["wall"]                elif self.maze[y][x] == "F":                    color = colors["finish"]                elif self.maze[y][x] == "S":                    color = colors["start"]                else:                    color = colors["background"]                arcade.draw_rectangle_filled(minimap_x + x * self.tile_size / 4 + self.tile_size / 8,                                              minimap_y + y * self.tile_size / 4 + self.tile_size / 8,                                              self.tile_size / 4,                                              self.tile_size / 4,                                              color)        # Отрисовка игрока на мини-карте        arcade.draw_circle_filled(minimap_x + player_x * self.tile_size / 4 + self.tile_size / 8,                                   minimap_y + player_y * self.tile_size / 4 + self.tile_size / 8,                                   5, arcade.color.RED)        # Отрисовка направления игрока        angle_rad = math.radians(player_angle)        arcade.draw_line(minimap_x + player_x * self.tile_size / 4 + self.tile_size / 8,                         minimap_y + player_y * self.tile_size / 4 + self.tile_size / 8,                         minimap_x + player_x * self.tile_size / 4 + self.tile_size / 8 + 20 * math.cos(angle_rad),                         minimap_y + player_y * self.tile_size / 4 + self.tile_size / 8 + 20 * math.sin(angle_rad),                         arcade.color.RED)    def draw_score(self, score):        # Отображение текста с очками в центре экрана        arcade.draw_text(f"Score: {score}",                         500,                         500,                         arcade.color.WHITE,                         font_size=20,                         anchor_x="center",                         anchor_y="center")